update.Script:
Select @ShipToSysRowID = b.ShipToSysRowID,
		@OrderRelQty = (a.OrderQty-1),
		@FocrelQty = a.FOCQty,
		@ShipByDate = b.ReqShipDate,
		@ShipToName = b.ShipToName,
		@OHCompany = c.CompanyName,
		@OHCustNum = b.CustNum,
		@OHCustID = b.CustID 
From OrderDtl a
INNER JOIN OrderHed b
ON a.OrderHedID=b.OrderHedID
INNER JOIN Company c
ON b.CompanyID=c.CompanyID
Where OrderDtlID=@OrderDtlID

insert.Script:
var success = this.validateInput();
var commandName = this.arguments().CommandName;
var triggerFieldName = this.arguments().Trigger;

// validate Quantity field
if (success) {
    // 5. The quanity must be greater than zero.
    success = [Quantity] != null && [Quantity] > 0;
    if (!success)
        this.result.focus('Quantity', 'The quantity must be greater than zero.');
}

update.Script:
var success = this.validateInput();
var commandName = this.arguments().CommandName;
var triggerFieldName = this.arguments().Trigger;

// validate OrderRelQty field
if (success) {
    // 5. The quanity must be greater than zero.
    success = [OrderRelQty] != null && [OrderRelQty] > 0;
    if (!success)
        this.result.focus('Quantity', 'The quantity must be greater than zero.');
}

var success = true;
var commandName = this.arguments().CommandName;
var triggerFieldName = this.arguments().Trigger;

// validate OrderRelQty field
if (success) {
    // 5. The quanity must be greater than zero.
    success = [OrderRelQty] != null && [OrderRelQty] > 0;
    if (!success)
        this.result.focus('Quantity', 'The quantity must be greater than zero.');
}

Select @ShipToSysRowID = b.ShipToSysRowID,		
		@ShipByDate = b.ReqShipDate,
		@ShipToName = b.ShipToName,
		@OHCompany = c.CompanyName,
		@OHCustNum = b.CustNum,
		@OHCustID = b.CustID 
From OrderDtl a
INNER JOIN OrderHed b
ON a.OrderHedID=b.OrderHedID
INNER JOIN Company c
ON b.CompanyID=c.CompanyID
Where OrderDtlID=@OrderDtlID

Select @ShipToSysRowID = b.ShipToSysRowID,		
		@ShipByDate = b.ReqShipDate,
		@ShipToName = b.ShipToName,
		@OHCompany = c.CompanyName,
		@OHCustNum = b.CustNum,
		@OHCustID = b.CustID ,
		@OrderRelQty = a.OrderQty
From OrderDtl a
INNER JOIN OrderHed b
ON a.OrderHedID=b.OrderHedID
INNER JOIN Company c
ON b.CompanyID=c.CompanyID
Where OrderDtlID=@OrderDtlID

IF @OrderHedBulkOrder = 1
BEGIN
	@OrderRelQty=0;
END

Select @ShipToSysRowID = b.ShipToSysRowID,		
		@ShipByDate = b.ReqShipDate,
		@ShipToName = b.ShipToName,
		@OHCompany = c.CompanyName,
		@OHCustNum = b.CustNum,
		@OHCustID = b.CustID ,
		@OrderRelQty = a.OrderQty
From OrderDtl a
INNER JOIN OrderHed b
ON a.OrderHedID=b.OrderHedID
INNER JOIN Company c
ON b.CompanyID=c.CompanyID
Where OrderDtlID=@OrderDtlID

IF @OrderHedBulkOrder = 1
BEGIN
	SET @OrderRelQty=0
END

