EXEC dbo.[FSC_CustSt_EM_V2] 
    @Company = 'ADMSB',
    @CustId = 'C1000034,C1000034',
     @SalesRepCode=NULL,
    @GroupCode=NULL, 
    @CutOffDate = '2019-06-30'

